import Web3 from 'web3';
require("dotenv").config();
import { Transaction } from 'ethereumjs-tx';
const rpcURL = 'https://ropsten.infura.io/v3/16c1a9ae02fc4856b28f116d862acc69'; 
const web3 = new Web3(rpcURL);
const account1 = "0x956927273De0C7908dbD850aAaa35f7D36264A91";  
//const account2 = "0xC0cd84a7060fA76dfcb7BeB7d0432F1b3606F0b2";



//const account1PK = Buffer.from(process.env.PRIVATE_KEY, "hex");
const account1PK = Buffer.from('72ebeb938c70280fe25ac5e1535aebb21c70c047abd9f9d291025c5113afec1c', "hex");
console.log(account1PK);
//var Tx = require('ethereumjs-tx')

web3.eth.getTransactionCount (account1, (err, txCount) => {

    data = ''

    const txObject = {
        nounce:     web3.utils.toHex(txCount),
        gasLimit:   web3.utils.toHex(5000000),
        gasPrice:   web3.utils.toHex(web3.utils.toWei('10', 'gwei')),
        data:       data
    } 


const tx = new Transaction(txObject, { chain: 'ropsten', hardfork : 'petersburg' }); 

//new Tx(txObject, {'chain':'ropsten'});
tx.sign(account1PK);

const serialize = tx.serialize();
const txHex = '0x' + serialize.toString('hex')

web3.eth.sendSignedTransaction(txHex, (err, txHash) => {
    if (!err) {
        console.log('Transaction was successful : ', txHash);
    } else {
        console.log('Transaction Error :', err);
    }
}) 
})
